UPDATE transfer_comment SET
	tc_content = #{tc_content},
	tc_date = TO_CHAR(sysdate, 'YYYY-MM-DD HH24:MI:SS')
WHERE transb_bno = #{transb_bno}
AND comm_no = #{comm_no}


UPDATE transfer_comment SET tc_deldate = TO_CHAR(sysdate, 'YYYY-MM-DD HH24:MI:SS')
WHERE transb_bno = #{transb_bno}
AND comm_no = #{comm_no}
