mapper.update:
UPDATE product
prod_name=#{prod_name}, prod_price=#{prod_price}, prod_status=#{prod_status},
WHERE prod_no = #{prod_no}

mapper.select:
SELECT
	order_no, order_tid
FROM
	orders



			order_tid = #{tid}


			order_no = #{order_no}

mapper.insert:
INSERT into orders(order_no, mem_no, prod_no, order_tid) SELECT orders_seq.NEXTVAL, A.* FROM(
select #{item.mem_no} as mem_no, #{item.prod_no} as prod_no, #{item.tid} as order_tid from dual
) A

INSERT into pay(pay_no, order_no, pay_type, pay_price, pay_status) SELECT A.* FROM(
select #{item.pay_no} as pay_no, #{item.ORDER_NO} as order_no, #{item.pay_TYPE} as pay_type, #{item.PROD_PRICE} as pay_price, 1 as pay_status from dual
) A
